// ストップウォッチpublic class StopWatch {    private long start = 0;     // スタート時間    private long stop = 0;      // 終了時間    private long elapsed = 0;   // 経過時間    // コンストラクタ    public void StopWatch()    {        start = 0;     // スタート時間        stop = 0;      // 終了時間        elapsed = 0;   // 経過時間    }        // ストップウォッチの開始    public void start()    {        start = System.currentTimeMillis();    }    // ストップウォッチの終了    public void stop()    {        stop = System.currentTimeMillis();        elapsed = stop - start;    }    // ミリ秒の時間を取得    public long getMillisec()    {        return elapsed;    }    // 経過時間をプリント    public void printTime()    {        System.out.printf("*** Elapsed = %d (Start = %d , Stop = %d)\n", elapsed, start, stop);    }}